const toUnicodeEscape = (str) => Array.from(str)
    .map((ch) => {
    const cp = ch.codePointAt(0);
    return cp > 0xffff
        ? `\\u{${cp.toString(16)}}`
        : `\\u${cp.toString(16).padStart(4, '0')}`;
})
    .join(' + ');
